SET @admin_id = (SELECT id FROM users WHERE role = 'admin' OR role = 'system_admin' OR role = 'super_admin' ORDER BY createdAt ASC LIMIT 1);

UPDATE organization_settings SET updatedBy = @admin_id;

DELETE FROM user_roles WHERE user_id != @admin_id;
DELETE FROM user_permissions WHERE user_id != @admin_id;
DELETE FROM password_reset_tokens WHERE userId != @admin_id;
DELETE FROM employees WHERE userId != @admin_id;
DELETE FROM users WHERE id != @admin_id;

SELECT id, name, email, role FROM users;

